import { configureStore } from "@reduxjs/toolkit";
import topicReducer from '../features/topics/topicSlice'
import quizzesReducer from '../features/quizzes/quizzesSlice';

export default configureStore({
  reducer: {
    topics: topicReducer,
  },
});
